import React from 'react'
import PropTypes from 'prop-types'
import swal from 'sweetalert';
import { BsSearch } from "react-icons/bs";
import { BrowserRouter as Router, Switch, Route, Link, useParams } from "react-router-dom";
import { useHistory } from 'react-router-dom';

const Categories = ({ categories, onRemove }) => {
    let history = useHistory();
    const onHandleRemove = (id) => {
        swal({
            title: "Thông Báo !",
            text: "Bạn có chắc xóa 1 bản ghi ?",
            icon: "warning",
            buttons: true,
            dangerMode: true,
        })
            .then((willDelete) => {
                if (willDelete) {
                    swal("Bản ghi đã được xóa !", {
                        icon: "success",
                    });
                    onRemove(id)

                } else {
                    swal("Đã hủy xóa bảng ghi !");
                }
            });

    }
    return (
        <div>
            {/* Page Heading */}
            <div className="d-sm-flex align-items-center justify-content-between mb-4">
                <h1 className="h3 mb-2 text-gray-800">Tables</h1>
                <a href="/admin/addForm/categories" class="text-light btn-wide btn-shadow btn btn-success ">
                    <span class="btn-icon-wrapper pr-1 opacity-7">
                        <i class="fa fa-plus"></i>
                    </span>Add New</a>
            </div>
            <div className=" mb-4" >
                <form className="form-inline" action="" >
                    <label style={{ marginRight: '15px' }}>Từ Khóa</label>
                    <div className="input-group input-group-sm">
                        <input className="form-control form-control-navbar" type="search" name="keyword" placeholder="Search..." defaultValue="" aria-label="Search" />
                        <div className="input-group-append">
                            <button className="btn btn-primary" type="submit">
                                <BsSearch />
                            </button>
                        </div>
                    </div>
                </form>
            </div>
            {/* DataTales Example */}
            <div className="card shadow mb-4">
                <div className="card-header py-3">
                    <h6 className="m-0 font-weight-bold text-primary">DataTables Categories</h6>

                </div>
                <div className="card-body">
                    <div className="table-responsive">
                        <table className="table table-bordered table-hover">
                            <thead class="bg-c-yellow text-light">
                                <tr>
                                    <th>ID</th>
                                    <th>Name</th>
                                    <th>Slug</th>
                                    <th>Show_Menu</th>
                                    <th>Option</th>
                                </tr>
                            </thead>
                            <tbody>
                                {categories.map((category, index) => (
                                    <tr key={index} className="text-dark">
                                        <td>{category.id}</td>
                                        <td>{category.name}</td>
                                        <td>{category.slug}</td>
                                        <td>{category.show_menu}</td>
                                        <td>
                                            <Link to={`/admin/update/category/${category.id}`} class="border-1 btn-transition btn btn-outline-primary mr-1">
                                                <span class="btn-icon-wrapper pr-1 opacity-7">
                                                    <i class="fa fa-edit"></i>
                                                </span>
                                    Sửa</Link>
                                            <button onClick={() => onHandleRemove(category.id)} class="border-1 btn-transition btn btn-outline-danger ">
                                                <fa name="trash" _nghost-serverapp-c2="" ng-reflect-name="trash" >
                                                    <i _ngcontent-serverapp-c2="" aria-hidden="true" class="fa fa-trash mr-" ng-reflect-klass="fa fa-trash" ng-reflect-ng-class="">
                                                        Xóa
                                                    </i></fa></button>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </div>
    )
}

Categories.propTypes = {

}

export default Categories
