import React from 'react';
import PropTypes from 'prop-types';
import { useForm } from "react-hook-form";
import { useHistory } from 'react-router-dom';
import swal from 'sweetalert';

const AddCate = props => {
    let history = useHistory();
    const { handleSubmit, register, errors } = useForm();
    // const [inputValue, setInputValue] = useState({});
    // const onHandleChange = (e) => {
    //     console.log(e.target.value);
    //     const { name, value } = e.target;
    //     setInputValue({
    //         ...inputValue,
    //         [name]: value
    //     });
    // }

    const onHandleSubmit = (data) => {
        // onAdd(data);
        // history.push("/admin/products");
        swal("Thông Báo", "Thêm Sản Phẩm Thành Công!", "success");
    }
    return (
        <div>
            <h1 className="h3 mb-2 text-gray-800">Thêm Sản Phẩm</h1>
            {/* DataTales Example */}
            <div className="card shadow mb-4">
                <div className="card-body">
                    <form action="" onSubmit={handleSubmit(onHandleSubmit)} enctype="multipart/form-data">
                        <div className="form-group">
                            <label htmlFor="inputProductName">Tên Danh Mục</label>
                            <input type="text" name="name" className="form-control"
                                ref={register({ required: true, minLength: 5 })}
                                id="inputProductName" />
                            {errors.name && errors.name.type === "required" && <span class="text-danger">Không được bỏ trống</span>}
                            {errors.name && errors.name.type === "minLength" && <span class="text-danger">Phải trên 5 ký tự</span>}
                        </div>
                        <div className="form-group">
                            <label htmlFor="inputProductName">Slug</label>
                            <input type="text" name="image" className=" form-control "
                                ref={register({ required: true })}
                                id="inputProductImage" />
                            {errors.image && errors.image.type === "required" && <span class="text-danger">Không được bỏ trống</span>}

                        </div>
                        <div className="FormFields-module--radioGroup--3idzI" aria-label="Developer" style={{ marginBottom: '20px' }}>
                            <label>Yes&nbsp;
                                <input type="radio" name="Developer" defaultValue="Yes" />
                            </label>
                            <label>No&nbsp;<input type="radio" name="Developer" defaultValue="No" />

                            </label>
                        </div>
                        <button type="submit" class="btn btn-outline-primary mr-2">Submit</button>
                        <a href="/admin/Products" class="border-1 btn-transition btn btn-outline-danger ">
                            Hủy
                            </a>
                    </form>
                </div>
            </div>

        </div>
    )
}

AddCate.propTypes = {

}

export default AddCate
