import React, { useState } from 'react'
import PropTypes from 'prop-types';
import { useForm } from "react-hook-form";
import { useParams, useHistory } from 'react-router-dom';
import swal from 'sweetalert';

const EditFormProduct = ({ products, onUpdate }) => {
    const { register, handleSubmit, errors } = useForm();
    let { id } = useParams();
    let history = useHistory();
    const product = products.find(product => product.id === id);

    const [currentProduct, setCurrentProduct] = useState(product);

    const onHandleSubmit = (data) => {

        onUpdate(id, currentProduct);
        history.push('/admin/products');
        swal("Thông Báo", "Sửa Sản Phẩm Thành Công!", "success");
    }
    const onHandleChange = e => {
        const { name, value } = e.target;
        setCurrentProduct({
            ...currentProduct,
            [name]: value
        })
    }
    return (
        <div>
            <h1 className="h3 mb-2 text-gray-800">Sửa Sản Phẩm</h1>
            <div className="card shadow mb-4">
                <div className="card-body">
                    <form action="" onSubmit={handleSubmit(onHandleSubmit)} enctype="multipart/form-data">
                        <div className="form-group">

                            <label htmlFor="inputProductName">Tên Sản Phẩm</label>
                            <input type="text" name="name" onChange={onHandleChange} className="form-control"
                                ref={register({ required: true, minLength: 5 })}
                                id="inputProductName"
                                defaultValue={currentProduct.name}
                            />
                            {errors.name && <span class="text-danger">Không Được Bỏ Trống !!!</span>}
                        </div>
                        <div className="form-group mb-4">
                            <label>Danh Mục</label>
                            <select name="categories" onChange={onHandleChange} className="form-control" ref={register({ required: true })}>
                                <option selected disabled value="0">Mời Chọn...</option>
                                <option value="1">Yes</option>
                                <option value="0">No</option>
                            </select>
                        </div>
                        <div className="form-group">
                            <div className="row">
                                <div className="col-4 offset-4">
                                    <img src={currentProduct.image} width="200" className="rounded" />
                                </div>
                            </div>
                            <label htmlFor="inputProductName">Ảnh Sản Phẩm</label>
                            <input type="text" name="image" onChange={onHandleChange} className="form-control" id="inputProductImage"
                                defaultValue={currentProduct.image}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="inputProductName">Giá Sản Phẩm</label>
                            <input type="number" name="price" onChange={onHandleChange} className="form-control"
                                ref={register({ required: true, maxLength: 99 })}
                                id="inputProductPrice"
                                defaultValue={currentProduct.price}
                            />
                            {errors.price && <span class="text-danger">Không Được Bỏ Trống !!!</span>}
                        </div>
                        <button type="submit" class="btn btn-outline-primary mr-2">Submit</button>
                        <a href="/admin/Products" class="border-1 btn-transition btn btn-outline-danger ">
                            Hủy
                            </a>
                    </form>
                </div>
            </div>



        </div>
    )
}

EditFormProduct.propTypes = {
    products: PropTypes.array
}

export default EditFormProduct
